#ifndef LEETCODE_2030_SMALLESTK_LENGTHSUBSEQUENCEWITHOCCURRENCESOFALETTER_H
#define LEETCODE_2030_SMALLESTK_LENGTHSUBSEQUENCEWITHOCCURRENCESOFALETTER_H

#include "solution.h"

class SmallestSubsequence : public solution {
public:
    string smallestSubsequence(string s, int k, char letter, int repetition) {
        string res = "";
        int len = s.size();
        int remain_letter = count(s.begin(), s.end(), letter);
        int curr_rep = count(res.begin(), res.end(), letter);
        for (int i = 0; i < len; ++i) {
            while (!res.empty() && (res.back() > s[i]) &&
                   ((curr_rep + remain_letter > repetition) || (res.back() != letter)) && (res.size() + len - i > k)) {
                if (res.back() == letter)
                    curr_rep--;
                res.pop_back();
            }
            if (res.size() < k) {
                if (s[i] == letter) {
                    res.push_back(s[i]);
                    curr_rep++;
                } else if (int(k - res.size()) > repetition - curr_rep) {
                    res.push_back(s[i]);
                }
            }
            if (s[i] == letter)
                remain_letter--;
        }
        return res;
    }

    void check() {
//        string s = "";
//        int k = 5608;
//        char letter = 'g';
//        int repetition = 883;
        string s = "leet";
        int k = 3;
        char letter = 'e';
        int repetition = 1;
        string res = smallestSubsequence(s, k, letter, repetition);
        cout << res << endl;
    }
};

#endif //LEETCODE_2030_SMALLESTK_LENGTHSUBSEQUENCEWITHOCCURRENCESOFALETTER_H
